contentCss:
@font-face {
  font-family: lato;
  src: url(https://socrathematics.github.io/Lato/Lato-Regular.ttf);
  font-weight: 100;
}

body {
  font-family: lato, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
  line-height: 1.4;
  margin: 1rem;
}



table {
  border-collapse: collapse;
}

table th,
table td {
  border: 1px solid #ccc;
  padding: .4rem;
}

figure {
  display: table;
  margin: 1rem auto;
}

figure figcaption {
  color: #999;
  display: block;
  margin-top: .25rem;
  text-align: center;
}

hr {
  border-color: #ccc;
  border-style: solid;
  border-width: 1px 0 0 0;
}

code {
  background-color: #f2f2f7;
  border-radius: 3px;
  padding: .1rem .2rem;
}

.mce-content-body:not([dir=rtl]) {
  blockquote {
    border-left: 2px solid #ccc;
    margin-left: 1.5rem;
    padding-left: 1rem;
  }
  background-color:#2c2c2e;
  border-radius:0.25rem;

}

// RTL
.mce-content-body[dir=rtl] {
  blockquote {
    border-right: 2px solid #ccc;
    margin-right: 1.5rem;
    padding-right: 1rem;
  }
}

initConfig:
tinymce.init({
  plugins: [
    "a11ychecker advcode casechange formatpainter",
    "linkchecker autolink lists checklist",
    "media mediaembed pageembed permanentpen",
    "powerpaste table advtable tinymcespellchecker"
  ],
  toolbar: "formatselect | bold italic strikethrough forecolor backcolor formatpainter | alignleft aligncenter alignright alignjustify | numlist bullist outdent indent | link insertfile image | removeformat | code | addcomment | checklist | casechange",
  height: 360,
  statusbar: false
});

lessVariables:
@background-color: rgba(56, 56, 59, 1);
@toolbar-background-color: rgba(56, 56, 59, 1);
@menubar-background-color: rgba(56, 56, 59, 1);
@border-color: rgba(56, 56, 59, 1);
@color-black: rgba(44, 44, 46, 1);
@color-white: rgba(202, 202, 202, 1);
@color-tint: rgba(40, 167, 69, 1);
@color-error: #c00;
@color-success: rgba(102, 197, 124, 1);
@base-value: 16px;
@control-border-radius: .25rem;
@panel-border-radius: 3px;
@tinymce-border-radius: 3px;
@font-stack: sans-serif;
@font-weight-normal: 300;
@font-weight-bold: bold;
@toolbar-button-icon-color: rgba(202, 202, 202, 1);
@toolbar-button-text-color: rgba(202, 202, 202, 1);
@toolbar-button-hover-background-color: rgba(44, 44, 46, 1);
@toolbar-button-hover-icon-color: rgba(202, 202, 202, 1);
@toolbar-button-hover-text-color: rgba(202, 202, 202, 1);
@toolbar-button-focus-background-color: @toolbar-button-hover-background-color;
@toolbar-button-focus-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-focus-text-color: @toolbar-button-hover-icon-color;
@toolbar-button-active-background-color: @toolbar-button-hover-background-color;
@toolbar-button-enabled-background-color: @toolbar-button-hover-background-color;
@toolbar-button-active-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-active-text-color: @toolbar-button-hover-icon-color;
@toolbar-button-enabled-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-enabled-text-color: @toolbar-button-hover-icon-color;
